#[test]
fn base64_test() {
    assert_eq!(b"".to_vec().base64(), b"".to_vec());
    assert_eq!(b"f".to_vec().base64(), b"Zg==".to_vec());
    assert_eq!(b"fo".to_vec().base64(), b"Zm8=".to_vec());
    assert_eq!(b"foo".to_vec().base64(), b"Zm9v".to_vec());
    assert_eq!(b"foob".to_vec().base64(), b"Zm9vYg==".to_vec());
    assert_eq!(b"fooba".to_vec().base64(), b"Zm9vYmE=".to_vec());
    assert_eq!(b"foobar".to_vec().base64(), b"Zm9vYmFy".to_vec());
}

